  // Getters and Setters.
  
  public void setCC(int cc) {
    carryFlag = (cc & C_MASK) != 0;
    overflowFlag = (cc & V_MASK) != 0;
    zeroFlag = (cc & Z_MASK) != 0;
    negativeFlag = (cc & N_MASK) != 0;
    irqDisableFlag = (cc & I_MASK) != 0;
    halfCarryFlag = (cc & H_MASK) != 0;
    firqDisableFlag = (cc & F_MASK) != 0;
    entireFlag = (cc & E_MASK) != 0;
  }

  public int getCC() {
    int cc = 0;
    if (carryFlag)
      cc |= C_MASK;
    if (overflowFlag)
      cc |= V_MASK;
    if (zeroFlag)
      cc |= Z_MASK;
    if (negativeFlag)
      cc |= N_MASK;
    if (irqDisableFlag)
      cc |= I_MASK;
    if (halfCarryFlag)
      cc |= H_MASK;
    if (firqDisableFlag)
      cc |= F_MASK;
    if (entireFlag)
      cc |= E_MASK;
    return cc;
  }

